# ./runLundPlane -hard "/data/yjlee/pyquen/ppPhotonJet150/pp_4.pu14" -hardtype PU14 -nev 2000 -output "/data/kying/jetTopicsData/ppPhotonJet24/pp_4.root" -type photonjet